import React, { useState } from 'react';
import { FaArrowRight } from 'react-icons/fa';  // Import the arrow icon

// Importing project images
import shellImage from '../assets/images/shell-project.jpg';  // Add the correct image path
import hbnbImage from '../assets/images/hbnb-project.jpg';    // Add the correct image path
import dwellingduoImage from '../assets/images/dwellingduo-project.jpg'; // Add the correct image path

const Projects = () => {
  const projects = [
    {
      title: 'Simple Shell Command Line Interpreter/Interface',
      description: 'A simple shell implementation to interact with the system.',
      details: 'Detailed information about the shell, features, and challenges encountered.',
      image: shellImage, // Using the imported image here
      githubUrl: 'https://github.com/Jayricka/simple_shell', // GitHub URL
    },
    {
      title: 'Hbnb; Airbnb Clone',
      description: 'A clone of Airbnb with similar features and user experience.',
      details: 'This project involved building a user-friendly platform for property listings.',
      image: hbnbImage,  // Using the imported image here
      githubUrl: 'https://github.com/Jayricka/AirBnB_clone', // GitHub URL
    },
    {
      title: 'Dwellingduo',
      description: 'A property listing web platform connecting tenants and landlords.',
      details: 'A platform for tenants and landlords to find rental properties and manage listings.',
      image: dwellingduoImage, // Using the imported image here
      githubUrl: 'https://github.com/Jayricka/dwellingduo', // GitHub URL
    },
  ];

  // State for modal
  const [isModalOpen, setModalOpen] = useState(false);
  const [activeProject, setActiveProject] = useState(null);

  const openModal = (project) => {
    setActiveProject(project);
    setModalOpen(true);
  };

  const closeModal = () => {
    setActiveProject(null);
    setModalOpen(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-4xl font-bold text-center text-gray-800">My Projects</h1>
      <p className="mt-4 text-center text-gray-600">Here are some of the projects I've worked on:</p>

      {/* Project cards */}
      <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project, index) => (
          <div
            key={index}
            className="bg-white shadow-lg rounded-lg overflow-hidden transition-transform transform hover:scale-105 hover:shadow-xl"
          >
            {/* Project Image */}
            <img
              src={project.image}
              alt={project.title}
              className="w-full h-56 object-cover mb-4 transition-transform transform hover:scale-105"
            />
            <div className="p-6">
              <h2 className="text-2xl font-semibold text-gray-800 hover:text-blue-600 transition-colors">{project.title}</h2>
              <p className="mt-2 text-gray-600">{project.description}</p>
              <button
                className="mt-4 text-blue-500 hover:text-blue-700 font-semibold transition-colors"
                onClick={() => openModal(project)}
              >
                View Details
              </button>
              {/* GitHub Link Button with Arrow */}
              <a
                href={project.githubUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="mt-4 block text-blue-500 hover:text-blue-700 font-semibold flex items-center space-x-2"
              >
                <span>Click to View</span>
                <FaArrowRight className="text-blue-500" />
              </a>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {isModalOpen && activeProject && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white w-11/12 md:w-2/3 lg:w-1/2 p-6 rounded-lg shadow-lg relative transition-transform transform hover:scale-105">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-700"
              onClick={closeModal}
            >
              &times;
            </button>
            <h2 className="text-3xl font-semibold text-gray-800">{activeProject.title}</h2>
            <p className="mt-4 text-gray-700">{activeProject.details}</p>
            <button
              className="mt-6 bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700"
              onClick={closeModal}
            >
              Close
            </button>
            {/* GitHub Link Button in Modal with Arrow */}
            <a
              href={activeProject.githubUrl}
              target="_blank"
              rel="noopener noreferrer"
              className="mt-4 block text-blue-500 hover:text-blue-700 font-semibold flex items-center space-x-2"
            >
              <span>Click to View</span>
              <FaArrowRight className="text-blue-500" />
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default Projects;
